import { StatusBar } from 'expo-status-bar';
import React ,{ useState} from 'react';
import { KeyboardAvoidingView, Platform, StyleSheet, Text, TextInput, TouchableOpacity, View } from 'react-native';
import Task from "./Tasks/Task";

export default function App() {
   const [taskText, settaskText] = useState(null);
   const [taskItem, settaskItem]:any = useState([]);
   const handleAddTask = () => {
     console.log(taskText)
     settaskItem([...taskItem,taskText]);
     settaskText(null);
   }

  return (
    <View style={styles.container}>
      <View style={styles.taskWrapper}>
         <Text style={styles.appTitle}><strong> ToDo Task List  </strong></Text>
          <View style={styles.taskItem}>

                  {taskItem.map((item:string) =>{
                    return    <Task text={item}/>
                  })
                  }
                  <Task text='Task 1'/>
                  <Task text='Task 1'/>
                  <Task text='Task 1'/>
          </View>
      </View>
      <KeyboardAvoidingView
      behavior={Platform.OS==='ios'?'padding':'height'}
      style={styles.writeTaskWrapper}
      >
        <TextInput style={styles.input} placeholder={'Add Task'} value={taskText} onChange={(text)=>settaskText(text) }></TextInput>
        <TouchableOpacity onPress={()=> handleAddTask()}>
          <View style={styles.addWrapper}>
            <Text style={styles.addText}>+</Text>
            </View>
        </TouchableOpacity>
      </KeyboardAvoidingView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#E8EAED',
  },
  taskWrapper:{
    paddingTop:80,
    paddingHorizontal:20
  },
  appTitle:{
    paddingTop:80,
    paddingLeft:20
  },
  taskItem:{
    marginTop:30
  },
  writeTaskWrapper:{
    position:'absolute',
    bottom:60,
    width:'100%',
    flexDirection:'row',
    justifyContent:'space-between',
    alignItems:'center'
  },
input:{
  paddingVertical:15,
  paddingHorizontal:15,
  width:250,
  backgroundColor:'#FFF',
  borderRadius:60,
  borderColor:'#C0C0C0',
  borderWidth:1
},
addWrapper:{
  width:60,
  height:60,
  borderRadius:60,
  backgroundColor:'#FFF',
  justifyContent:'center',
  alignContent:'center',
  borderColor:'#C0C0C0',
  borderWidth:1
},
addText:{},
});
